// compact version of https://www.shadertoy.com/view/4dcSWs

void mainImage(out vec4 O,  vec2 U){

    vec2 z = iResolution.xy;                                // normalized coordinates
         U = (U+U - z) / z.y;

	z = U - vec2(-1,0);  U.x -= .5;                         // Moebius transform
    U *= mat2(z,-z.y,z.x) / dot(U,U);

                   // offset   spiral, zoom       phase     // spiraling
    U =   log(length(U+=.5))*vec2(.5, -.5) + iTime/8.
        + atan(U.y, U.x)/6.3 * vec2(5, 1);        
	                                 // n  
  if (mod(iTime,2.)<1.) 
      O += length(fract(U*3.)) -O;
  //  U = fract(U*3.), O = sin(6.28*max(U.x,U.y)+vec4(0,2.1,-2.1,0)); // colored
  //  O += 1. - length(fract(U*3.)-.5) - O;  // by Shane
  else 
      O  = texture(iChannel0, U*3.);
  //  O += length(sin(U*30.)) -O;
}
