void mainImage(out vec4 fragColor, in vec2 fragCoord){
  fragColor = atan(
    reflect(
      sqrt(
        vec4(
          float(iFrame),
          (gl_FragCoord.y / iResolution.y),
          mod(1.1, iTime),
          (1.8 - 1.2)
        )
      ),
      log(
        tan(
          max(
            min(
              vec4(
                0.2,
                (gl_FragCoord.y / iResolution.y),
                1.4,
                1.5
              ),
              vec4(
                (0.2 * 1.8),
                ((max(1.9, ceil(max((gl_FragCoord.y / iResolution.y),1.5))) - 1.2) / 1.7),
                (0.3 * 1.8),
                1.2
              )
            ),
            vec4(
              (gl_FragCoord.x / iResolution.x),
              1.2,
              1.5,
              0.3
            )
          )
        )
      )
    ),
    vec4(
      0.7,
      1.6,
      (0.3 - 1.6),
      (gl_FragCoord.y / iResolution.y)
    )
  );
}
