// Produce a 1 if the provided value is <= 0, with a small transitional blur
#define S(v) smoothstep(blur, 0., v)

// Draw (return 1) a circle at the origin with the radius of r. 
#define circle(U, r) S(length(U) - r)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1) so that 0,0 is the center
    vec2 uv = ( 2.*fragCoord - iResolution.xy ) / iResolution.y;
    float blur = 4./iResolution.y;

    float mask = 0.;

    for (int i=0; i < 54; i++) {
        vec2 st = vec2(cos(float(i) * iTime * uv.x), sin(float(i) * iTime * uv.y));
        mask += circle(st, .2);
    }

    // Output to screen
    fragColor = vec4(vec3(mask),1.0);
}
