float circle(vec2 uv, vec2 pos, float r){

    pos = uv-pos;
    float d = length(pos);
    //return smoothstep(r, r-0.1, d);
    return r/d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float c1 = circle(uv, vec2(sin(iTime)*0.05, 0.), 0.1);
    float c2 = circle(uv, vec2(sin(iTime*1.3)*-0.05, 0.), 0.1);
    float c3 = circle(uv, vec2(0., sin(iTime*1.2)*0.05), 0.1);
    vec3 circle1 = c1 * vec3(1.0, 0.3, 0.8);
    vec3 circle2 = c2 * vec3(0.0, 1.0, 0.);
    vec3 circle3 = c3 * vec3(0.0, 0.3, 1.);
    vec3 col = circle1+circle2+circle3;

    // Output to screen
    fragColor = vec4(col,1.0);
}
