void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.xy -= 0.5;

    vec3 col = vec3(0);

    // Colour displayed
    vec3 ncol =vec3(sin(iTime / 2.),sin(iTime),sin(iTime * .2));

    vec3 bumpmap = vec3(sin((uv.x + sin(iTime/50.)*5.) * 20.));

    //bumpmap + ncol
    bumpmap += ncol;   

    // Output to screen
    fragColor = vec4(bumpmap,1.0);
}
