// Animating a work by https://www.instagram.com/jgbentley10/ who you should check out!
#define AMPLITUDE 0.1
#define SPEED 1.5

float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
    float fl = floor(p);
    float fc = fract(p);
    return mix(rand(fl), rand(fl + 1.0), fc);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float amp = (1. + sin(iTime * SPEED)) * .5 * AMPLITUDE;
    //amp = noise(iTime * SPEED) * AMPLITUDE; 
    uv.y += noise(iTime + uv.x * 50.) * amp;
    uv.x += noise(iTime + uv.y * 25.) * amp;

    uv = clamp(uv, 0., 1.);

    fragColor = texture(iChannel0, uv);
}
