#define RES iResolution
#define deltaTime iTimeDelta


float plot(float st, float pct, float lineWidth)
{

	return smoothstep( pct - lineWidth, pct, st) - smoothstep( pct, pct + lineWidth, st);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/RES.xy;  
    vec3 color = vec3(0.0, 0.0, 0.0);

    // Line Equation
    float y = st.x;

    // Plot a line
    float pct = plot(st.y,y, 0.005);
    color = (1.0 - pct) * color + pct * vec3(1.0,0.0,0.0);

    pct = plot(st.y,y, 0.005);
    color = (1.0 - pct) * color + pct * vec3(1.0,1.0,0.0);

	fragColor = vec4(color,1.0);
}
