// N-Ary Sine-Tree - written 2015 by Jakob Thomsen
// (a variant of https://www.shadertoy.com/view/4dGGWm)
// With improvments by FabriceNeyret2

#define levels 3.

void mainImage( out vec4 o, vec2 U )
{
    U /= iResolution.xy; 
    U.y *= levels;

    float n = mod(ceil(iDate.w), 6.) + 1.,
          r = .5-.5*cos(fract(U.y)*3.14),
          s = pow(n, floor(U.y)),
          x = fract(U.x*s) - .5 ;

    //o = vec4( abs(x/r) > .5 ? 1. : smoothstep(0.,n*s/4e2, r*abs( fract( x*n/r + n/2. ) - .5))); // simple but with gaps
    float xx = x*n/r + n/2. ,
    ss = n*s/r / 4e2 ;
    o = vec4( min(1., abs( (abs(x/r) > .5 ? xx : fract(xx) ) - .5) / ss )); // FN2's "pretty printing" solution by redrawing
}
