//Original Shader https://www.shadertoy.com/view/llSGDh

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // create pixel coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ab = uv;
    uv = (2.*fragCoord - iResolution.xy) / iResolution.y;

	//uv = uv-0.5;
    uv = vec2 ( sqrt(uv.x*uv.x + uv.y*uv.y), atan(uv.y, uv.x) );
	// first texture row is frequency data
	float fft  = texture( iChannel0, vec2(uv.x,0.25) ).x; 

    // second texture row is the sound wave
	float wave = texture( iChannel0, vec2(ab.x,0.75) ).x;

	// convert frequency to colors
	vec3 col = vec3(1.0);
    col = vec3(smoothstep(0.0, 1.0, fft))*fft;
	if(uv.x > 1.0)
    {
        col = col * vec3(wave, 0.3, 0.3);
        //col = vec3(0);
    }
    // add wave form on top	
	float col2 = 1.0 -  smoothstep( 0.0, 0.01, abs(fft -ab.y ));
  	if(abs(uv.x) >1.0)
    {
        col2 = 0.0;
    }

    //col += 1.0 -  smoothstep( 0.0, 0.01, abs(wave - ab.y-0.3) );

    //col = pow( col, vec3(5.0,1.0,5.0) );
	//col = col * mix(vec3(abs(sin(iTime))*uv.x), vec3(abs(sin(iTime))*uv.x), uv.x);
    col = col*vec3(col2*300.0, 1.0, 1.0);//*(0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)));//, col2*30., 1.0);
    //col = pow(col, vec3(col2)*10.0);
	// output final color
	fragColor = vec4(col, 1.0);
}
