void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

float total (vec3 col){
    return col.r+col.g+col.b;
}
float checkCol (vec2 pos, vec2 t){
    return total(texture(iChannel0, pos+t).rgb);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pix = 1./iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;
    if(checkCol(uv, vec2(-pix.x, 0.))>0.&&total(texture(iChannel0, uv).rgb)==0.){
        col = texture(iChannel0, uv-vec2(pix.x, 0.)).rgb-0.005;
    }
    if(iMouse.xy!=vec2(0.)){ // if its next to the mouse
        if (length(uv-mouse)<0.02){
            col = texture(iChannel1, uv).rgb; //change buffer picture to white
        }
    } else {
        col = texture(iChannel0, uv).rgb;//otherwise, keep it the way it was
    }



    fragColor = vec4(col, 1.0);
}
