float smoothedge(float v) {
    return smoothstep(0.0, 4.0 / iResolution.y, v);
}

float circle(vec2 p, float radius) {
  return length(p) - radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 1.5;
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    uv *= scale;

    float xRange = scale * (iResolution.x / iResolution.y);    
    float x = uv.x - iMouse.x / iResolution.x * xRange * 2. + xRange;
    float y = uv.y - iMouse.y / iResolution.y * scale * 2. + scale;

    float d = circle(vec2(x, y), 0.25);

    vec3 c = vec3(smoothedge(d));
    fragColor = vec4(c,1.); 
}
