struct Dreieck 
{
    vec2 pos,
        width,
        height;

    vec4 col;
};

Dreieck dro = Dreieck (vec2 (0.+ cos(iTime),.6),vec2 (.15,.0), vec2 (0.,.5), vec4(1.,0.5,.25,1.)),
    	drre = Dreieck (vec2 (.33+cos(iTime),.27),vec2 (.5,.0), vec2 (0.,.15), vec4(1.,0.5,.25,1.)),
   		drri = Dreieck (vec2 (.29 +cos(iTime),.26),vec2 (.37,.0), vec2 (0.,.09), vec4(1.,0.5,.25,1.)),
    	droi = Dreieck (vec2 (-0.014 +cos(iTime),.55),vec2 (.09,.0), vec2 (0.,.37), vec4(0.,1.5,.0,1.));

vec4 Hiteck (in vec2 p_uv, in Dreieck p_dr){

        vec2 xynew = p_uv - p_dr.pos;
        float
        x = dot(xynew, p_dr.width)/dot(p_dr.width, p_dr.width),
        y = dot(xynew, p_dr.height)/dot(p_dr.height, p_dr.height); 

    if (abs(x)>.5 || abs(y) >.5)
    { 
        return vec4(.0);
    }
     if (x+y < 0.0 )
        return p_dr.col;
    return vec4 (.0);

} 

Dreieck drl = Dreieck (vec2 (-.325 +cos(iTime),.265),vec2 (.5,.0), vec2 (0.,.15), vec4(1.,0.5,.25,1.)),
    	drli = Dreieck (vec2 (-.29 +cos(iTime),.28),vec2 (.37,.0), vec2 (0.,.09), vec4(1.,0.5,.25,1.)),
   		dru = Dreieck (vec2 (0.003 +cos(iTime),-.065),vec2 (.15,.0), vec2 (0.,.5), vec4(1.,0.5,.25,1.)),
    	drui = Dreieck (vec2(0.01 +cos(iTime),-.035),vec2 (.09,.0), vec2 (0.,.37), vec4(0.,1.5,.0,1.));

      vec4 Hiteck2 (in vec2 p_uv, in Dreieck p_dr){

        vec2 xynew = p_uv - p_dr.pos;
        float
        x = dot(xynew, p_dr.width)/dot(p_dr.width, p_dr.width),
        y = dot(xynew, p_dr.height)/dot(p_dr.height, p_dr.height); 

    if (abs(x)>.5 || abs(y) >.5)
    { 
        return vec4(.0);
    }
     if (x+y > 0.0 )
        return p_dr.col;
    return vec4 (.0);

}              

Dreieck rm = Dreieck (vec2(0.0045 +cos(iTime),.267),vec2 (.145,.0), vec2 (0.,.14), vec4(1.,0.5,.25,1.));


 vec4 Hiteck3 (in vec2 p_uv, in Dreieck p_dr){

        vec2 xynew = p_uv - p_dr.pos;
        float
        x = dot(xynew, p_dr.width)/dot(p_dr.width, p_dr.width),
        y = dot(xynew, p_dr.height)/dot(p_dr.height, p_dr.height); 

    if (abs(x)>.5 || abs(y) >.5)
    { 
        return vec4(.0);
          }
     if (x*x+y*y < .275*.275)
         return p_dr.col;
     return vec4 (0.);
   }              

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*(fragCoord.xy / iResolution.xy-0.5)*vec2 ( iResolution.x/iResolution.y, 1.);
	fragColor = vec4(0.2,.5,.65,1.);

 vec4 col =  Hiteck( uv, dro)-Hiteck(uv, droi)+Hiteck(uv, drre)-Hiteck(uv, drri)+Hiteck2( uv, drl)-Hiteck2( uv, drli)+Hiteck2(uv,dru)-Hiteck2(uv,drui)+Hiteck3(uv,rm);

      if (col.a < .9)
        fragColor = vec4(0.2,.5,.65,1.0);
      else
    fragColor = col;

}
