void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 p = -1.+2.*uv;
    p.x*=iResolution.x/iResolution.y;

    vec3 col = vec3(0.);
    float len = length(p);

    col += pow(exp(1.),-pow(len,12.));
	col *= vec3(1.-len*len,1.-len,len*len);


    fragColor = vec4(col,1.);
}
