void mainImage( out vec4 O,  vec2 U )
{
	O = texture(iChannel0, U/iResolution.xy);
}

//#define rnd(x) fract(3213.*sin((x+iTime)*mat2(1,13.51,73.37,-57.17)))
//#define rnd(x) fract(3e4  *sin((x+iTime)*mat2(1,13.51,73.37,-57.17)))
  #define rnd(x) fract(      sin((x+iTime)*mat2(1,13.51,73.37,-57.17)) / 9e-4)
  #define srnd(x) sign(2.*rnd(x)-1.)
  #define T(d) texture(iChannel0, (U+d)/R)

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;
     if (iFrame==0) {                  // --- initial state
         O = vec4(U-.5==R/2.);         // center seed
         //if (rnd(U).x<.1) O.r = 1.;    // some dust
         return; 
     }

	O = T(0.); if (O==vec4(1)) return; // coral remains once formed

    vec2 P = (U+U-R)/R.y;              // --- new random walkers
    if (length(P) > .5 && rnd(U).x<.01) 
 // if (rnd(U).x< .001*pow(length(P),50.)) 
 // if (O.b < 1e-6 && rnd(U).x<0.001) 
                                        { O.r = 1.; return; }

    for (int j=-1; j<=1; j++)
        for (int i=-1; i<=1; i++) {    // --- dust random walk 
            vec2 d = vec2(i,j);
            vec4 T = T(d);
            if (O.r==1. && T==vec4(1)) { O = T; return; }  // dust touch coral: aggregate
            if (T.xy==vec2(1,0) && srnd(U+d)==-d) { O = T; return; } // walker propagates
        }

    O -= O;
    // blue: pseudo distance to coral
    O.b = ( T(vec2(-1,0)).b + T(vec2(1,0)).b + T(vec2(0,-1)).b + T(vec2(0,1)).b ) / 4.;
}
