/* order 3 (197c) by FabriceNeyret2
void mainImage( out vec4 f, vec2 u )
{
    u /= 1e2;
    u.y = (2.*u.y-u.x)/2.2;

    vec3 g = vec3(u,1.7-u.x-u.y);  // try others than 1.7
	g = abs(fract(g)-.5);
	f = f - f + max(max(g.x, max(g.y,g.z)), min(g.x, min(g.y,g.z))*2.);
}/**/

