// variables for the grid size
float numX = 8.0;
float numY = 5.0;
vec4 color1=vec4(1.,0.,1.,1.);
vec4 color2=vec4(0.,1.,1.,1.);
float animateSpeed=.3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float total = (floor(uv.x * numX) + floor(uv.y * numY));

    float mixFactor=0.;
	if(animateSpeed!=0.){
    	mixFactor=sin(iTime*animateSpeed)/2.+0.5;
    }
    if (mod(total, 2.0) == 0.0){fragColor = mix(color1,color2,mixFactor);}
    else{fragColor = mix(color2,color1,mixFactor);}
}
