// Thanks to ABizard, FabriceNeyret2 and GregRostami for golfing tips
void mainImage(out vec4 O, vec2 U)
{
    float r = 15. / iResolution.y,
          q = mod(ceil((U *= r).x - .3), 2.);
    O = vec4(7, 1, 2, 4) * .1;
    O = (U.x < 11.7 && U.y > 7. ? O.zzwy : sin(2.69 * U.y) < 0. ? O/O : O)
      + smoothstep(r,
                   0.,
                   length(U = vec2(fract(clamp(U.x + .7, 1., 12.)),
                                   mod(clamp(U.y, 8. - q, 14. + q ) - q * .8, 1.62)) - .5)
                   * cos(abs(mod(atan(U.x, U.y) + .6, 1.26) - .6) - 1.26) - .1);
}
