#define Dots 20
#define MB 4
#define PI 3.1415926535897932384626433832795

vec2 rotate(vec2 p, float a)
{
	return vec2(p.x * cos(a) - p.y * sin(a), p.x * sin(a) + p.y * cos(a));
}

vec3 col_pal(float x)
{
	vec3 col = mix(vec3(1.0, 0.0, 0.0), vec3(1.0, 0.5, 0.0), smoothstep(0.125, 0.25 ,x));
	col = mix(col, vec3(1.0, 1.0, 0.0), smoothstep(0.25 , 0.375,x));
	col = mix(col, vec3(1.0, 1.0, 0.0), smoothstep(0.25 , 0.375,x));
	col = mix(col, vec3(0.0, 1.0, 0.0), smoothstep(0.375, 0.5  ,x));
	col = mix(col, vec3(0.0, 0.0, 1.0), smoothstep(0.5  , 0.625,x));
	col = mix(col, vec3(0.3, 0.0, 0.5), smoothstep(0.625, 0.75 ,x));
	col = mix(col, vec3(0.56,0.0, 1.0), smoothstep(0.75 , 0.875,x));
	col = mix(col, vec3(1.0, 0.0, 0.0), smoothstep(0.875, 1.0  ,x));

	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv * 2.0 -1.0;
	uv.x *= iResolution.x / iResolution.y;

	vec3 col = vec3(length(uv * 0.25));

	for(int j = 0; j < MB; j++)
	{
		for(int i = 0; i < Dots;i++)
		{
			vec2 p = vec2(uv.x,uv.y + 0.5 + 0.4 * sin(iTime * 5.0 + (iTime * 0.0 + 6.) * PI / float(Dots) * float(i+ 1) + float(j + 1) / float(MB)));

			uv.xy = rotate(uv.xy, 2. * PI / float(Dots));

			col = mix( col, mix(col_pal(float(i + 1) / float(Dots)),vec3(length(uv * 0.25)),1.0 - float(j + 1) / float(MB)), 1.0-smoothstep(0.020,0.025,length(p)) );
		}
	}

	fragColor = vec4(col,1.0);
}
