float remap(float x) {
    return x * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float freq = 5.0;
    float s = sin(uv.x*6.28*freq);
    float c = (dFdx(s) * iResolution.x/(6.28*freq));
	fragColor = vec4( uv.y < 0.5 ? remap(c) : remap(s), 0., 0.,1.0);
}
