void mainImage( out vec4 c, vec2 f )
{    
    c-=c;
    float i = 1.,m,a;
    vec3 p;
    while(i++ < 9.)
        p = (vec3(f/iResolution.x,1)-.5)*i + vec3(m=0.,1,-3),
        a = pow(j j j j j j,length(p)*5.),            
        c.rgb += a*sin(p+iTime*vec3(.1,.2,.7))*.5+a*a*2.;    
}

