vec4 circle(vec2 uv, vec2 center, float radius)
{
/*    if(distance(uv, center) > radius)
    {
        return vec4(0);
    }
    return vec4(1);
*/
    //use step to replace if statements 
    return vec4( step ( distance(uv, center), radius )) ;
}

mat2 rotate(float angle)
{
    return mat2(cos(angle), -sin(angle), 
                sin(angle), cos(angle));
}

vec4 rotateCircle(vec2 uv, vec2 d, float r)
{
    vec2 center1 = d;    
    center1 = rotate(iTime)*center1 +iResolution.xy*0.5;
    return circle(uv, center1,r);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //init fragColor with zero
    fragColor -= fragColor;
    vec2 uv = fragCoord.xy;

    for(int i = -4; i < 5; ++i)
    {
        fragColor+= rotateCircle(uv, vec2(0,50.0*float(i)) ,sin(iTime*2.0)*10.0+20.0);
    }

    for(int i = -4; i < 5; ++i)
    {
        fragColor+= rotateCircle(uv, vec2(50.0*float(i),0) ,sin(iTime*2.0)*10.0+20.0);
    }


}
