vec4 Ball( vec2 pos, vec2 uv, vec4 col, float k ) {
	return vec4( 1.3 - vec3( pow( length( pos * 5. + 5. - uv * 10. ), .2 ) ), 1. ) * col * k;
}
vec4 Noise( sampler2D tex, vec2 uv ) {
	vec4 n0 = textureLod( tex, uv, 0. ) * .03125;
    vec4 n1 = textureLod( tex, uv * .5, 0. ) * .0625;
    vec4 n2 = textureLod( tex, uv * .25, 0. )* .125;
    vec4 n3 = textureLod( tex, uv * .125, 0. )* .25;
    vec4 n4 = textureLod( tex, uv * .0625, 0. )* .5;
    vec4 n5 = textureLod( tex, uv * .03125, 1. );
    return ( n0 + n1 + n2 + n3 + n4 + n5 ) / 1.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float _k = sin( iTime * 2. );
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 ball0 = Ball( vec2( sin( iTime ) * _k, cos( iTime ) * _k ), uv, vec4( 1., 1., cos( iTime - 123. ), 1. ), 10. );
    vec4 ball1 = Ball( vec2( sin( iTime - 123. ) * _k, - cos( iTime + sin( iTime * .1 + 123. ) ) * _k ), uv, vec4( sin( iTime - 123. ), 1., 0., 1. ), 10. );
    vec4 ball2 = Ball( vec2( cos( iTime - 123. ) * _k, - sin( iTime + sin( iTime * .1 + 123. ) ) * _k ), uv, vec4( cos( iTime - 123. ), 1., 1., 1. ), 10. );

    vec4 ball3 = Ball( vec2( sin( iTime ) * _k, cos( iTime ) * _k ), uv, vec4( 1., 0., sin( iTime ), 1. ), 10. );
    vec4 ball4 = Ball( vec2( sin( iTime - 434. ) * _k, - cos( iTime + sin( iTime * .1 + 564. ) ) * _k ), uv, vec4( sin( iTime ), 0., 1., 1. ), 10. );
    vec4 ball5 = Ball( vec2( cos( iTime - 534. ) * _k, - sin( iTime + sin( iTime * .5 + 643. ) ) * _k ), uv, vec4( cos( iTime ), 1., 0., 1. ), 10. );

    vec4 ball6 = Ball( vec2( sin( iTime ) * _k, cos( iTime ) * _k ), uv, vec4( 1., 0., sin( iTime + 7674. ), 1. ), 10. );
    vec4 ball7 = Ball( vec2( sin( iTime - 946. ) * _k, - cos( iTime + sin( iTime * .51 + 84565. ) ) * _k ), uv, vec4( sin( iTime - 53347. ), 0., 0., 1. ), 10. );
    vec4 ball8 = Ball( vec2( cos( iTime - 546. ) * _k, - sin( iTime + sin( iTime * .5 + 45645. ) ) * _k ), uv, vec4( cos( iTime ), 1., 0., 1. ), 10. );

    vec4 balls = ball0 + ball1 + ball2 + ball3 + ball4 + ball5 + ball6 + ball7 + ball8;
	fragColor = balls * Noise( iChannel0, uv + iTime ) + Noise( iChannel1, uv ) * 1.;
}
