// now with a slightly more interesting pattern
#define INVERTED
vec2 rot(vec2 p) {
	return vec2(p.x*0.6-p.y*0.4,p.x*0.4+p.y*0.6);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy*(1.0/128.0);
	float res=min(iResolution.x,iResolution.y);
	float pixel=1.0/res;
	vec2 p = (fragCoord.xy-iResolution.xy*0.5) * pixel;	
	float h= texture(iChannel0,uv).x;
	float h2=texture(iChannel0,uv+vec2(0.001,0.003)).x;

	vec4 ink=vec4(0.0);
	float r=0.45;
	float t=iTime*0.84+463.0;
	vec2 col0=vec2(sin(t*0.00215),cos(t*0.0013623));
	vec2 col1=vec2(sin(t*0.00315),cos(t*0.0023623));
	vec2 col2=vec2(sin(t*0.00415),cos(t*0.0033623));
	ink.w+=  smoothstep(0.0,1.0,iTime)*smoothstep(pixel*1.,0.0,abs(length(p)-0.47))*0.5;
	ink.w+=  smoothstep(0.0,1.0,iTime)*smoothstep(pixel*1.,0.0,abs(length(p)-0.07))*0.5;

	float inv=5.*dot(p,p);
	if (inv<1.0) 	
	{ 		
		vec2 oldp=p;

#ifdef INVERTED		
		p/=inv;
		//p*=1.5;		
		//p*=0.88;
		p=mix(p,oldp,0.5+0.5*cos(max(0.0,iTime*0.1-1.0)));
#endif		
		vec2 dx=vec2(dFdx(p.x),dFdy(p.x));
		vec2 dy=vec2(dFdx(p.y),dFdy(p.y));
		float pixel2 = max(length(dx),length(dy));

		ink.xyz+=smoothstep(1.5,3.5,iTime)*texture(iChannel2,col0).xyz*smoothstep(r+pixel2,r,length(fract(p-vec2(0.0,-0.2))-0.5));
		p+=col0*2.0;
		p=rot(p);
		ink.xyz+=smoothstep(4.0,6.0,iTime)*texture(iChannel2,col1).yzx*smoothstep(r+pixel2,r,length(fract(p-vec2( 0.2*0.866,0.1))-0.5));
		p+=col2+0.5;
		p=rot(p);
		ink.xyz+=smoothstep(6.5,8.5,iTime)*texture(iChannel2,col2).zxy*smoothstep(r+pixel2,r,length(fract(p-vec2(-0.2*0.866,0.1))-0.5));
		ink.xyz*=smoothstep(0.04,0.04+pixel,inv);
	}

	vec3 maskcol=texture(iChannel1,uv*0.12+0.1).xyz;
	maskcol*=texture(iChannel1,uv*0.1335+0.32).zyx;
	maskcol*=texture(iChannel0,uv*2.0).x*0.5+0.5;
	maskcol+=0.1*length(p);
	vec3 mask=smoothstep(0.95,0.7,maskcol)*0.1+0.9;
	ink*=mask.xxxx;
	ink.xyz+=ink.w;

	vec3 col = vec3(0.75+(h-h2)*0.2) * smoothstep(1.0,0.0,ink.xyz);
	col=mix(col,vec3(dot(col,vec3(0.3,0.6,0.1))),vec3(0.3));//desat
	col*=vec3(0.99,0.98,0.97);
	//col=(mask);


	fragColor=vec4(pow(col,vec3(0.55)),0.0);

}
