#ifdef GL_ES
precision highp float;
#endif


#define time iTime
#define resolution iResolution




#define cx 0.0
#define cy 0.0

#define gtime iTime

#define pi 3.14159



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 v = fragCoord.xy / iResolution.xy;
    v.x -= 100.0*(cos(gtime*0.005));;
    v.y -= 150.0*sin(gtime*0.01);
    vec3 color = vec3(0.0,0.0,0.0);


    float x = 0.1*abs(sin(gtime));
    float grid = abs(cos(0.5 - v.x)*sin(0.5 - v.y))*sin(gtime*0.2)*0.4;


    float psy = abs(sin(v.y*pi*grid));
    float psx = abs(sin(v.x*pi*grid));

    float pr = psx*psy*(sin(gtime));
    float pg = psx*psy*(sin(gtime));
    float pb = psx*psy;

    float r = pow(x,pr)*sin(v.y);
    float b = pow(x,pg)*sin(v.x);
    float g = pow(x,pb);


    color = vec3(r,g,b);

    fragColor = vec4(color,1.0);

}
