void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = vec4(0,0,0,0);
    float denominator = 0.;

    float amount = 1.0 - (sin(uv.x*5.0 + iTime*6.0)*0.5+0.5) * (sin(uv.y*3.5 + iTime*4.0)*0.5+0.5);
    amount = pow(amount,5.0);

    const float intensity = 10.0;
    vec2 pixelWidth = vec2(1.0)/iResolution.xy * intensity;
    const int size = 8;
    for (int x=-size; x<size; x++) {
        for (int y=-size; y<size; y++) {

        	float fx = float(x) * amount*pixelWidth.x;
           	float fy = float(y) * amount*pixelWidth.y;
			fragColor += texture(iChannel0, uv + vec2(fx,fy));        
        	denominator += 1.0;
        }
    }

    fragColor /= denominator;

}
