float Distance(vec2 p1, vec2 p2)
{
    return sqrt((p2.x - p1.x)*(p2.x - p1.x) + (p2.y - p1.y)*(p2.y - p1.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float distance = Distance(fragCoord.xy / iResolution.xy, iMouse.xy / iResolution.xy);
    fragColor = mix(vec4(0.0, 0.0, 0.0, 1.0), vec4(1.0, 1.0, 1.0, 1.0), distance);
}
