const float pi = 3.14159,
    two_pi = pi*2.0,
    half_pi = pi/2.0;

const float zoom = 2.0;
const float spirality = 4.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy - 0.5;
    vec2 mxy = iMouse.xy/iResolution.xy - 0.5;
    vec2 offset = xy - mxy;

    float f = two_pi*zoom;
    float dif = length(offset);
    float dir = atan(offset.x, offset.y) + (iTime*dif)/(two_pi);
    float str = sin((dif + dir*pi*spirality)*f);

    vec4 col = texture(iChannel0, vec2(offset));
    fragColor = mod(vec4(str, str, str, 1.0), col);

}
