void mainImage(out vec4 fragColor, in vec2 fragCoord)
{ 
	vec2 uv = (fragCoord.xy -0.5 * iResolution.xy) / iResolution.x;
    vec2 c = vec2(0.0, 0.0);
    float radius = length(uv) * 2.0 + iTime/10.0;

    float a = mod(radius, 0.1);

    float blend = smoothstep(0.02, 0.03, a) * (1.0 - smoothstep(0.07, 0.08, a));

	fragColor = vec4(blend, 0.0, blend, 1.0);
}
