void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float decay = 0.985;
    vec4 buf = texture(iChannel0, uv) * decay;

    float ar = iResolution.x / iResolution.y;
    uv = uv * 2.0 - 1.0;
    uv.x *= ar;

    float t = iTime * 0.8;

    int samples = 32;

    float mainParticle = 0.0;
    float eps = 0.0025;

    for(int i = 0; i < samples; ++i)
    {
        float steps = float(i) * eps;
        vec2 pos = vec2(sin(mod(t + steps, 3.141592 * 2.0)),
                    cos(mod(t + steps, 3.141592 * 2.0)));

    	pos *= (sin((t + steps) * 10.0) * 0.25 + 0.5);

    	float dist = length(uv + pos);
    	float main = 1.0 - clamp(dist * 40.0, 0.0, 1.0);
    	float glow = (1.0 - clamp(pow(dist, 0.15) * 0.95, 0.0, 1.0)) * 0.0075;

    	mainParticle += main + glow;
    }

    mainParticle *= 0.15;

    //Random colour things...
    vec4 col = vec4(1.0);
    col.r += -sin(iTime);
    col.g *= cos(iTime);
    col.b *= sin(iTime);

    //Avoid black
    col.rgb += 0.25;
    col.rgb = normalize(col.rgb);

    fragColor =  buf + vec4(mainParticle) * col;
}
