void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float amt = 0.3;

	vec2 uv = fragCoord.xy/iResolution.xy;
	vec2 nuv = uv.xy;


    if(amt!=0.0){

    	if(uv.y>=.5)	nuv.y = ((uv.y - .5) * (((uv.y/2.0) + amt * sin(1.57 * uv.x * 2.0))) * 2.0) + .5;
    	else			nuv.y = ((uv.y - .5) * ((((uv.y-1.0)/2.0) - amt * sin(1.57 * uv.x * 2.0))) * -2.0) + .5;

    }
    nuv.y += iTime/2.0;

	fragColor = texture(iChannel0, nuv);
}
