// main
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    //vec2 pxoffset = vec2(mod(iTime, 10.)*-30., 0.);
    vec2 pxoffset = vec2(0.);
    vec2 uv2 = (fragCoord.xy - pxoffset) / iResolution.xy;
    vec3 c = texture(iChannel0, uv2).xyz;

	c = step(vec3(0.5), c);

    fragColor = vec4(c.rgb,1.0);
}

// buf A
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 firstFrameImg = texture(iChannel3, uv).xyz;
    vec3 bufBcolor = texture(iChannel1, uv).xyz;;
    vec3 c;
    // Imprint image periodically
    float frameIndex = mod(float(iFrame),10.);
    if (  frameIndex == 1. ) {
        bufBcolor = firstFrameImg;

    }

	// Copy Buffer B into Buffer A
    fragColor = vec4(bufBcolor.rrr,1.0);
}

// buf b
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    //float pxIndex = mod(iTime, 20.);
    float pxIndex = mod(iTime, iMouse.x * 10.);
    //float pxIndex = (1.) ;
    vec2 lastuv = vec2((fragCoord.x - pxIndex), fragCoord.y) / iResolution.xy;

    vec3 a = texture(iChannel0, uv).xyz;
    vec3 am1 = texture(iChannel0, lastuv).xyz;

    vec3 err;
    if (step(.5, am1.r) > .5){
    	err = vec3(1.) - am1;
    } else {
    	err = vec3(0.) - am1;
    }

    err *= 0.5; // This slows down the devolution
    vec3 o;

    o = a + err;
    //o = err ;

    fragColor = vec4(o.rgb,1.0);
}
