void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(0.0, 0.5, 0.8);
    vec3 col = vec3(1.0, 0.5, 0.5);
    float GlobalTime = mod(iTime + floor((r.y - 0.5) * 2.5) * 0.5, 1.6) * 15.0 - 11.5;

    r *= 5.0;
    r.y = mod(r.y - 1.0, 2.0) - 1.0;

    r.y /= 0.5;
    float i = smoothstep(0.5, max(0.8 - (r.x - GlobalTime) * 0.3 / 0.5, 0.5), length(r - vec2(GlobalTime, 0.0)));
    ret = col * (1.0 - i) + ret * i;

	fragColor = vec4(ret,1.0);
}
