vec3 signal (float lo, float ho, float l, vec3 col, vec2 p, float time) {
    return  col *= abs (l/((p.y)*(800.0*cos(time))+cos(p.x*lo)*ho));
}

/*

At the beginning I wrote an article "How to seduce with maths" (ðŸ˜‚) for the school magazine (this year). 
It describes how to draw a heart in "GeoGebra" with an equation that I've found. 
After I saw the video of Inigo Quilez dealing with this subject : his equation is x10000 better than mine !
His awesome tutorial : https://www.youtube.com/watch?v=aNR4n0i2ZlM    ðŸ˜Š

Mine : XÂ²+(a*Y-abs(X))Â²=b+c*Y*cos(atan2(d*X,Y)*e)

*/

float FonctionCoeur (float x, float y,float t, float scaleY, float scaleX) {
    t=t*3.0;
    return pow(x*scaleX+(cos(1.2)*.005),2.0) + pow((y* scaleY+(sin(t)*.002)- abs(x) - (abs(x) / (x-10.0))),2.0)-.2*y*cos(atan(1.0*x,y)*.6);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/ iResolution.x;  // Not correct, I know ðŸ˜‰
    float sound = texture( iChannel0, vec2(uv.x,1.0)).x;
    float taille = .02;
    float t = iTime;
    float x = .5;
    if(t<11.55) {
     	x=.1+abs(cos(t));   
    }
    vec2 pos = uv-vec2(x,.2+cos(t)/50.0); 
    if (t<63.0&&t>50.0) {
    	taille = .001;    
        pos.y = uv.y-.4;
        pos.x= uv.x+cos(t);
    }
    vec3 lightdirection = vec3 (uv.x*uv.y);
    vec3 couleur = vec3 (1.0,uv.x*.5,uv.y*.5);
    couleur+= vec3 (abs(pos.x));
    float diffusion = 2.0;
    float i = 5.0;
    vec3 img = couleur*vec3(0.2,.6,0.9);
    vec3 col = vec3(0.9,0.4,0.0);
    if (t<75.0) {
    	img+= vec3 (signal (5.0,9.0*sound*sound*19.0,1.0,vec3 (2.0),vec2 (t,.15)-uv, 0.5))*col;
    	img+= vec3 (signal (3.0,9.0,sound,vec3 (1.5),vec2 (t,.22)-uv, 0.5))*col;
    	img+= vec3 (signal (10.0,18.0*sound*sound,1.0,vec3 (1.0),vec2 (t,.35)-uv, 0.5))*col;
    	img+= vec3 (signal (3.0,9.0,1.0,vec3 (2.5,0.0,0.0),vec2 (t,.36)-uv, 0.5))*col;
    }
	img*=1.0+cos(sound)*.05;
    img*=(.6+vec3(.49,.5,.9)*cos(sound)*.15 )*1.0;
    img*=.8;
    img+= vec3 (mod(floor(pos.x*t)+floor(pos.y*t),2.0))*.1;
    vec3 col1 = img;
    float xa = pos.x;
    if (t<62.0&&t>50.0) {
    	xa = mod(abs(pos.x),cos(t));
    }
    if (FonctionCoeur(xa,pos.y,t,1.5,1.2) < taille+abs(cos(t*7.0))*.02) {
    	img = couleur*lightdirection*lightdirection*diffusion*i+vec3(0.1,pos.y*.1,0.0);
        img+=vec3(pos.x*.9,0.0,0.0)*cos(sound);
        if(t<50.0&&t>40.0) {
         	img=col1;   
        }
        if (t<11.5) {
         	img=col1;   
        }
        if (t>75.0) {
         	img=col1;   
        }
    }
    if (t<75.0) {
        float azerty = 4.0; //Why this name, I don't know ðŸ˜
        if (t>45.0&&t<55.0) {
         	azerty+=cos(t)*6.0;   
        }
    	img+= vec3 (signal (5.0,8.0,1.0,vec3 (0.0,azerty,1.0),vec2 (t,.28)-uv, 0.5))*col;
    	img+= vec3 (signal (9.0,18.0,1.0,vec3 (2.1),vec2 (t,.33)-uv, 0.5))*col;
    }
    img+= vec3 (signal (1.2,sound*sound*80.0,1.0,vec3 (6.0),vec2 (0.0,.05)-uv, 0.5))*col;
    if (t>83.0) {
    	img*=0.0;   
    }
    if(t>63.0&&t<75.0) {
     img+= vec3 (0.0,.2+sin(t)*.2,.5+cos(t)*.4);   
    }
	fragColor = vec4(img*(lightdirection+.6)*clamp(t*.07,0.0,1.0),1.0);
}
