float line(vec2 p, vec2 p1, vec2 p2);

#define FONTW 8.
#define LINE(x1, y1, x2, y2) fragColor += line(fragCoord, vec2(totalW + x1, totalH + y1), vec2(totalW + x2, totalH + y2));
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int w = 80 + 80 + 30 + 30 + 100;
    const int h = 100;
    int totalW = int((iResolution.x / 2.) - float((w / 2)));
    int totalH = int((iResolution.y / 2.) - float((h / 2)));

    // H
    LINE(0, 100, 0, 0)
    LINE(5, 50, 45, 50)
    LINE(50, 100, 50, 0)

    totalW += 80;

    // E
    LINE(5, 0, 45, 0)
    LINE(5, 50, 45, 50)
    LINE(5, 100, 45, 100)
    LINE(0, 0, 0, 100)

    totalW += 80;

    // L
    LINE(0, 0, 0, 100)

    totalW += 30;

    // L
    LINE(0, 0, 0, 100)

    totalW += 30;

    // O
    vec2 c = vec2(totalW + 50, totalH + 50);
    float dist = distance(fragCoord.xy, c);
    fragColor += (dist > 50.? max((50. + FONTW) - dist, 0.) : dist - (50. - FONTW));

    totalW += 100;

    fragColor.a = 1.;
}

float line(vec2 p, vec2 p1, vec2 p2) {
    vec2 a = p;
    vec2 o = p1;
    vec2 b = p2;

    a -= o;
    b -= o;

    float alen = length(a);
	float blen = length(b);

    float cosalen = dot(a, b) / blen;
    float value = 0.;

    if(cosalen <= 0.) {
        value = alen;
    }
    else if(cosalen >= blen) {
        value = distance(a, b);
    }
    else {
        float cosA = cosalen / alen;
        float sinA = sqrt(1. - (cosA * cosA));
        value = alen * sinA;
    }

    value = max(FONTW - value, 0.);
    return value;
}
