void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    vec2 uv;

    float h = 0.45;
    uv.x = h * p.x / p.y;
    uv.y = 0.1 * iTime + h / abs(p.y);

    fragColor = vec4(texture(iChannel0, uv).xyz * p.y * p.y, 1.0);
}
