#define time iTime

float smin(float a, float b, float k)
{
	float h = clamp(.5 + .5*(a-b)/k, 0., 1.);
	return mix(a, b, h) - k*h*(1.-h);
}

float map(vec2 uv)
{
	float c0 = length(uv-vec2( .7*sin(2.*time),	.4*cos(1.*time)	))-.1;
	float c1 = length(uv-vec2( .7*sin(1.*time),	.4*cos(3.*time)	))-.1;
	return smin(c0, c1, .5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((2.*fragCoord.xy-iResolution.xy)/iResolution.y)/1.4;
	float t = 1./iResolution.y;
	float c = smoothstep(-t,t,map(uv))-length(uv)/8.;
	fragColor = vec4(c);
}
