float map(vec3 p){
    p.xy*=rz2(iTime*.5);p.yz*=rz2(iTime*.7);
    p.xz+=rz2(p.y*2.*sin(iTime))*vec2(1.,0.);
    return length(p.xz)-.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y;
    ro=vec3(uv,-10.);
    rd=normalize(vec3(uv,1.));
    mp=ro;
    for(int i=0;i<50;++i){
        d=map(mp);
        if(d<.001)break;
        mp+=d*rd*.5;
    }
	fragColor = vec4(length(mp-ro)*.05);
}
