void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float d = texture(iChannel0, uv + vec2(0.2*sin(iTime),0.)).r;
	vec3 c = vec3(1.0, 0.4, 0.0) *  d;

    d = texture(iChannel0, uv).r;
	c += vec3(0.4, 1.0, 0.0) * d;

    d = texture(iChannel0, uv - vec2(0.2*sin(iTime),0.)).r;
	c += vec3(0.0, 0.4, 1.0) * d;

    c *= 4.0;

	fragColor = vec4(c,1.0);
}

#define tau 6.283185
#define num 15

float circle(vec2 p, float r)
{
	return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float d = 0.97 * clamp(texture(iChannel0, uv).r, 0.0, 1.0);

    uv = -1.0 + 2.0 * uv;
    uv.x *= iResolution.x/iResolution.y;

    uv *= 15.;

    for(int n = 0; n < num; ++n)
    {
        float progress = float(n)/float(num);
        float amplitude = 15. * sin(iTime);
     	vec2 p = uv + amplitude * vec2(cos(iTime + tau * progress), sin(iTime + tau * progress));
		d = max(d, 1. - circle(p, 0.1));       
    }

    fragColor = vec4(d);
}
