void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float v = smoothstep(0.2,.9,length(uv-.5));

    fragColor = abs(uv.y-.5) < .4 ? (1.-v)*texture(iChannel0,uv) : vec4(0) ;

    //agColor += v;
}

float ls(vec2 u, vec2 s, vec2 e)
{
    vec2 l1 = u-s;
    vec2 l2 = (e-s)*(clamp( dot(l1,(e-s) ) /(dot(e-s,e-s)), 0., 1. ) );

    return 1.-smoothstep(.003,.005,length(l1-l2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    vec2 uv = u;

    u += .1;

    float cs = clamp(tan(iTime),0.,.005);

    u += cs*sin(iTime*vec2(89,23))+.01*sin(iTime*vec2(1,2));
    vec4 c = texture(iChannel1,u);
    vec4 f = c;

    u -= .1;

    float m = f.g-.8*f.r > .4 ? 0. : 1.;

    vec4 t = m*texture(iChannel0,(c.r+c.b+c.g)*.33+u);

    vec4 p = (1.-m)*texture(iChannel2,u*.99);

    vec2 su = (2.*fragCoord-iResolution.xy)/iResolution.x;

    float s = 0.;

    s += .02/length(su-vec2(.3));

    su = fract(su*8.+.42*sin(u.yx*34.24+4.*iTime))*4.-2.;

    s += (.2*cs+.01)/(length(sin(vec2(0,1.4)+.3*iTime)*.3-su));

    float fr = mod(floor(iTime*10.),3.)+2.;

    vec2 npos = sin(vec2(0,1.4)+.6*iTime)/4.+vec2(.4,.3);

    float a = 0.1*sin(iTime*2.);

    float rs = sin(a), rc = cos(a);

    uv *= mat2(rc,rs,-rs,rc);
    uv += .02*sin(uv.yx*16.+iTime*4.);

    vec4 nya = texture(iChannel3,( ((clamp(uv-npos,vec2(0,0.),vec2(.4,1.)))/vec2(-5,1))*2.+vec2(fr/6.4,0)) );
    //nya.rgb = .5+.5*sin(nya.rgb*3.+32.*length(uv-npos-vec2(.2,.3))+5.*iTime);

    float g = .006+.004*sin(iTime);

    nya.rgb += g/length( (uv-npos-vec2(.146,.222)) )*vec3(1,.2,.2);
    nya.rgb += g/length(uv-npos-vec2(.08,.22))*vec3(1,.2,.2);

    nya.rgb -= ls(uv-npos,vec2(.128,.23),vec2(.15,.25));
    nya.rgb -= ls(uv-npos,vec2(.105,.23),vec2(.08,.25));

    vec4 w = s*(.5+.5*sin(sin(vec4(3,1,2,6)*3.*uv.yxyx+iTime)*3.+uv.xyxy*8.));

    t += .3*fwidth(t);

    fragColor = 1.2*t+(1.-nya.a)*.8*p+(1.-m)*(1.-nya.a)*w+(1.-m)*nya.a*nya;
}
