// inspired from https://www.youtube.com/watch?v=Jri0del_6t4


// Gabor kernel
#define G(U,a,v) exp(-.5*dot(U,U)*1e3) * sin( 100.*dot(U,sin(a+vec2(1.57,0)))-10.*(v)*sin(iDate.w) ) 


void mainImage( out vec4 O, vec2 U )
{
	U = (U+U - (O.xy=iResolution.xy) ) / O.y;
    O -= O;

    for (float i=-2.; i<3.; i++)    // 5 pairs of lines
        for (float j=-4.; j<5.; j++)     // made of 9 splats
            O +=   G( U-vec2(.8* i    , j/5.), 0.  , j)
                 + G( U-vec2(.8*(i+.5), j/5.), 0.  ,-j);

	O = .5 + .5*O; 
}
