void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec2 center = vec2(0.0, 0.0);

    float ringRadius = 0.3 + sin(iTime) / 15.0;
    float ringThickness = 0.05;
    float halfThickness = ringThickness / 2.0;

    float pxDist = distance(uv, center);

    float innerColor = smoothstep(ringRadius, ringRadius - halfThickness, pxDist);
    float outColor = smoothstep(ringRadius, ringRadius + halfThickness, pxDist);

    float col = innerColor + outColor;
    col = 1.0 - col;

    fragColor = vec4(vec3(col),1.0);
    fragColor *= vec4(1.0, 1.5, 2.0, 1.0);
}
