#define click iMouse.z > 0.0
#define speed 0.05
void mainImage( out vec4 O, in vec2 f ) {
	vec2 uv = f / iResolution.xy;
    float m =  iMouse.x / iResolution.x,
        t = click ? m : fract(iTime*speed);
    uv.x = click ? (uv.x < t ? t : uv.x) : t;        
    O =  texture(iChannel0, uv);
}
