// variant of https://shadertoy.com/view/4dVfWD

void mainImage( out vec4 O, vec2 U )
{
    float n = 12.*U.x/iResolution.x;
    ivec2 I = ivec2(U);

    O =   n < 1. ? vec4( pow(.5,1./2.2) )                   // perceptual mid-grey
        : n < 2. ? vec4( ( I.x + I.y ) % 2 )                // 1-pix checkboard
        : n < 3. ? vec4( ( iFrame + I.x + I.y ) % 2 )       // same time-switching 60fps
        : n < 4. ? vec4( ( iFrame/2 + I.x + I.y ) % 2 )     // same time-switching 30fps
        :          vec4( ( iFrame + I.x/32 + I.y/32 ) % 2 );// time-switching big checkboard
}
