const vec3 offsets = normalize(vec3(5.0, 2.0, 1.0));

void mainImage(out vec4 out_color, vec2 coord) {
	vec2 uv = coord / iResolution.xy;
	vec3 color = texture(iChannel0, uv).rgb;

    float s;
    if (iMouse.w > 0.0)
        s = iMouse.x / iResolution.x * 2.0 - 1.0;
	else
        s = sin(iTime);

    float t = pow(abs(s), 14.0) * sign(s);

    color = clamp(color + t * offsets * 8.0, 0.0, 1.0);
	out_color = vec4(color, 1.0);
}
