#define Frame float(iFrame)
#define Time iTime
//#define PixelCount iResolution.xy
#define OUT

#define rsqrt inversesqrt
#define clamp01(x) clamp(x, 0.0, 1.0)
#define If(cond, resT, resF) mix(resF, resT, cond)


const float Pi = 3.14159265359;
const float Pi2 = Pi * 2.0;
const float Pi05 = Pi * 0.5;

float Pow2(float x) {return x*x;}
float Pow3(float x) {return x*x*x;}
float Pow4(float x) {return Pow2(Pow2(x));}

vec2 AngToVec(float ang)
{	
	return vec2(cos(ang), sin(ang));
}

float SqrLen(float v) {return v * v;}
float SqrLen(vec2  v) {return dot(v, v);}
float SqrLen(vec3  v) {return dot(v, v);}
float SqrLen(vec4  v) {return dot(v, v);}


#define FUNC4_FLOAT(f)							\
vec2 f(vec2 v) {return vec2(f(v.x ), f(v.y ));}	\
vec3 f(vec3 v) {return vec3(f(v.xy), f(v.z ));}	\
vec4 f(vec4 v) {return vec4(f(v.xy), f(v.zw));}	\

#define FUNC4_UINT(f)								\
uvec2 f(uvec2 v) {return uvec2(f(v.x ), f(v.y ));}	\
uvec3 f(uvec3 v) {return uvec3(f(v.xy), f(v.z ));}	\
uvec4 f(uvec4 v) {return uvec4(f(v.xy), f(v.zw));}	\

float GammaEncode(float x) {return pow(x, 1.0 / 2.2);}
FUNC4_FLOAT(GammaEncode)

float CheapHash(float v)
{
    return fract(sin(v) * 43758.5453) * 2.0 - 1.0;
}

float CheapHash(vec2 v)
{
	return CheapHash(v.y + v.x * 12.9898);
}

float CheapHash(vec3 v)
{
	return CheapHash(v.xy + v.z * vec2(3.354355, 7.23432));
}

float CheapHash(vec4 v)
{
	return CheapHash(v.xyz + v.w * vec3(5.08452, 1.3456, 9.235621));
}

struct Cam
{
	vec3 Front, Right, Up;
	float Aspect;
	float AxisLen;	
};

Cam NewCam(vec2 ang, float fov, float aspect)
{
    Cam cam;

    float sinPhi   = sin(ang.x);
    float cosPhi   = cos(ang.x);
    float sinTheta = sin(ang.y);
    float cosTheta = cos(ang.y);    

    cam.Front = vec3(cosPhi * cosTheta, 
                              sinTheta, 
                     sinPhi * cosTheta);

    cam.Right = vec3(-sinPhi, 0.0, cosPhi);
    cam.Up    = cross(cam.Right, cam.Front);

    cam.Aspect = aspect;
    cam.AxisLen = aspect * tan(Pi05 - fov * 0.5);

    return cam;
}

// tc [-1..1]
vec3 NewRay(Cam cam, vec2 tc)
{
    tc.x *= cam.Aspect;

    vec3 imgPos = cam.Front * cam.AxisLen + (cam.Right * tc.x + cam.Up * tc.y);

    vec3 dir = normalize(imgPos);

    return dir;
}


/*
IN:
	rp		: ray start position
	rd		: ray direction (normalized)

	sp2		: sphere position
	sr2		: sphere radius squared

OUT:
	t		: distances to intersection points (negative if in backwards direction)

EXAMPLE:	
	vec2 t;
	float hit = Intersect_Ray_Sphere(pos, dir, vec3(0.0), 1.0, OUT t);
*/
float Intersect_Ray_Sphere(
vec3 rp, vec3 rd, 
vec3 sp, float sr2, 
out vec2 t)
{	
	rp -= sp;

	float a = dot(rd, rd);
	float b = 2.0 * dot(rp, rd);
	float c = dot(rp, rp) - sr2;

	float D = b*b - 4.0*a*c;

	if(D < 0.0) return 0.0;

	float sqrtD = sqrt(D);
	// t = (-b + (c < 0.0 ? sqrtD : -sqrtD)) / a * 0.5;
	t = (-b + vec2(-sqrtD, sqrtD)) / a * 0.5;

	// if(start == inside) ...
	if(c < 0.0) t.xy = t.yx;

	// t.x > 0.0 || start == inside ? infront : behind
	return t.x > 0.0 || c < 0.0 ? 1.0 : -1.0;
}

vec2 CmplxMul(vec2 c0, vec2 c1)
{
    return vec2(c0.x*c1.x - c0.y*c1.y, c0.y*c1.x + c0.x*c1.y);
}

vec2 CmplxMul(vec2 c0, float ang)
{
    vec2 c1 = vec2(cos(ang), sin(ang));

    return vec2(c0.x*c1.x - c0.y*c1.y, c0.y*c1.x + c0.x*c1.y);
}

float lerp(float a, float b, float l) {return a + (b - a) * l;}

float SCurveC1(float x) {return x*x*(3.-2.*x);}
FUNC4_FLOAT(SCurveC1)

float SCurveC2(float x){ return ((x * 6.0 - 15.0) * x + 10.0) * x*x*x;}
FUNC4_FLOAT(SCurveC2)


