float VoronoiN3(vec3 x, vec3 cells)
{
	vec3 ix = floor(x);
	vec3 fx = x - ix;

	float l0 = 16.0;
	float l1 = 16.0;

	vec3 v0;
	vec3 v1;

	for (float i = 0.0; i < 3.0; ++i)
	for (float j = 0.0; j < 3.0; ++j)
	for (float k = 0.0; k < 3.0; ++k)
	{
		vec3 v = vec3(i, j, k);
		vec3 c = v - 0.5;
		vec3 vp = v + ix;

        vp = mod(vp, cells);// mod(x, y) == x - y * floor(x/y)    
        vp = If(lessThan(vp, vec3(0.0)), cells + vp, vp);

		float s0 = CheapHash(vp * 1.3287 + 0.1338);
		float s1 = CheapHash(vp * 0.9562 + 1.4267);
		float s2 = CheapHash(vp * 0.8422 + 1.0456);

		//float3 off = Sample::Sphere(s0, s1);
		vec3 off = normalize(vec3(s0, s1, s2));

		c += off * 0.396;

		float l = SqrLen(fx - c);

		if (l < l0)
		{
			l1 = l0;
			v1 = v0;

			l0 = l;
			v0 = v;
		}
		else
		if (l < l1)
		{
			l1 = l;
			v1 = v;
		}
	}

	float res = l1 - l0;

	return res;
}
//=================================================================================================//
/////////////////////////////////////////////////////////////////////////////////////////////////////


float Fbm(vec3 p, vec3 off)
{
    const float count = 8.0;

    const float ang = Pi * (3.0 - sqrt(5.0));
    const vec2 rot = vec2(cos(ang), sin(ang));

    float res = 0.0;
    float accu_w = 0.0;
    float w = 1.0;
    //float aniS = 1.0;

    for(float i = 0.0; i < count; ++i)
    {
        float v = VoronoiN3(p, vec3(8.0));

        res += v * w;

        accu_w += w;

        //aniS *= 1.8;        
        w *= 0.7;
        p *= 1.6;
        p.xy = CmplxMul(p.xy, rot);
        p = p.yzx;
        p += off;
    }

    res /= accu_w;

    return res;
}


//#define USE_FBM

float EvalSceneCol(
    vec3 rp, Cam cam, vec2 uv0, bool right)
{
    float col = 0.0;
    vec2 PixelCount = iResolution.xy * vec2(0.5, 1.0);

    vec2 tex = uv0.xy / PixelCount;
    vec2 tex21 = tex * 2.0 - vec2(1.0);

    if(right)
    {      
        vec3 rd = NewRay(cam, tex21);

        vec2 t; vec3 n; vec3 a;
        float hit = Intersect_Ray_Sphere(rp, rd, vec3(0.0), 1.0, OUT t);


        if(hit > 0.0)// RIGHT ----------------------------------------------------------------------
        {
			vec3 p = rp + rd * t.x;           

            col = VoronoiN3(p * 8.0, vec3(4.0));

            #ifdef USE_FBM
            col = Fbm(p * 12.0, vec3(0.0));
			#endif
        }
        else
        {
           col = 0.11;           
        }
    }
    else// LEFT ------------------------------------------------------------------------------------
    { 
        uv0 -= PixelCount * 0.5;
        uv0 *= 0.04 + length(rp)*0.04;  

        vec3 uvw;
        uvw = uv0.x * normalize(vec3(0.5, -1.0, 0.5)) + 
              uv0.y * normalize(vec3(1.0, 0.0, -1.0));

        //uvw.xy = -uvw.xy;
        //uvw.xy = vec2(-uvw.y, uvw.x);
        //uvw.z = -uvw.z;
        float n = VoronoiN3(uvw * 0.25, vec3(4.0));

        #ifdef USE_FBM
        n = Fbm(vec3(uv0 * .5, 0.), vec3(0.0));
        #endif

        col = n;
    }

    col = pow(clamp01(col), 2.2);
    return col;
}


void mainImage( out vec4 outCol, in vec2 uv0 )
{
    vec3 col = vec3(0.0);

    vec2 uv = uv0.xy - 0.5;

    vec2 PixelCount = iResolution.xy * vec2(0.5, 1.0);

    bool right = uv.x > PixelCount.x;
    if(right) uv.x -= PixelCount.x;


    vec4 mouseAccu = texelFetch(iChannel0, ivec2(1, 0), 0); 
    vec4 wasdAccu  = texelFetch(iChannel0, ivec2(2, 0), 0); 

    vec2 lightAng = vec2(Pi * 0.0, Pi * 0.5);
    lightAng.x += (wasdAccu.y - wasdAccu.w) * 0.06; 
    lightAng.y += (wasdAccu.x - wasdAccu.z) * 0.04;    


    vec2 ang = vec2(0.0, 0.0);
    ang += mouseAccu.xy * 0.008;

    Cam cam = NewCam(ang, Pi * 0.5, PixelCount.x / PixelCount.y);
    vec3 cpos = -cam.Front * 2.0;
    cpos = -cam.Front * (exp2(0.6 + mouseAccu.w * 0.02));


	#ifdef USE_AA
    float flip = step(0.5, fract(uv.y * 0.5));

    col += EvalSceneCol(cpos, cam, uv + vec2(abs(flip - 0.5  ), 1./6.), light, right);
    col += EvalSceneCol(cpos, cam, uv + vec2(abs(flip - 5./6.), 0.5  ), light, right);
    col += EvalSceneCol(cpos, cam, uv + vec2(abs(flip - 1./6.), 5./6.), light, right);    

    col /= 3.0;   
    #else
    col = vec3(EvalSceneCol(cpos, cam, uv, right));    
    #endif



    //if(col.x > 1.0) col = vec3(1.0, 0.0, 1.0);
    //if(col.x >

	outCol = vec4(GammaEncode(clamp01(col)), 1.0);
}

void mainImage( out vec4 col, in vec2 uv0 )
{  
    col = vec4(0.0);

    vec2 uv = uv0 - 0.5;


    vec4 iMouseLast     = texelFetch(iChannel0, ivec2(0, 0), 0);
    vec4 iMouseAccuLast = texelFetch(iChannel0, ivec2(1, 0), 0);
    vec4 wasdAccuLast   = texelFetch(iChannel0, ivec2(2, 0), 0);

    bool shift = texelFetch(iChannel1, ivec2(16, 0), 0).x != 0.0;

    float kW = texelFetch(iChannel1, ivec2(0x57, 0), 0).x;
    float kA = texelFetch(iChannel1, ivec2(0x41, 0), 0).x;
    float kS = texelFetch(iChannel1, ivec2(0x53, 0), 0).x;
    float kD = texelFetch(iChannel1, ivec2(0x44, 0), 0).x;

    vec4 wasdAccu = wasdAccuLast + vec4(kW, kA, kS, kD);

    vec2 mouseDelta = iMouse.xy - iMouseLast.xy;

    bool cond0 = iMouse.z > 0.0 && iMouseLast.z > 0.0;
    vec2 mouseDelta2 = cond0 && !shift ? mouseDelta.xy : vec2(0.0);
    vec2 mouseDelta3 = cond0 &&  shift ? mouseDelta.xy : vec2(0.0);

    vec2 iMouseAccu1 = iMouseAccuLast.xy + mouseDelta2;
    vec2 iMouseAccu2 = iMouseAccuLast.zw + mouseDelta3;

    if(uv.x == 0.0 && uv.y == 0.0) col = iMouse;  
    if(uv.x == 1.0 && uv.y == 0.0) col = vec4(iMouseAccu1, iMouseAccu2);
    if(uv.x == 2.0 && uv.y == 0.0) col = wasdAccu;
}
