void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 red = vec4(1.0, 0.0, 0.0, 1.0);
    vec4 black = vec4(0.0, 0.0, 0.0, 1.0);
    float zoom = 40.0;
    fragCoord = fragCoord/zoom;

    int heart[8] = int[](0, 16, 56, 124, 254, 254, 238, 68);
    if ((fragCoord.x >= 8.0) || (fragCoord.y >= 8.0)) {
     	fragColor = black;
        return;
    }


    int i = int(fragCoord.y);
    int pointmask = heart[i];
    for (int r = 0; r < int(fragCoord.x); r++) {
      pointmask = pointmask/2; //left shift.
    }

    if (pointmask % 2 == 1) {
   	 	fragColor = red;
    } else {
        fragColor = black;
    }
}
