// Mandelbrot mit Auto. Zoom in und Zoom out
// und das Ã¤ndern der Farben zu der Musik(Sound an machen)


const float colorspeed1 = 0.25;
const float colorspeed2 = 0.15;
const float rotspeed = 0.33;
const float aint = 4.0;


void mainImage ( out vec4 fragColor, in vec2 fragCoord )

{

	vec2 ax = fragCoord.xy / iResolution.x;

    //ax.x and ax.y Camera Pos.
    //Start Pos Camera
    ax.x -= 0.65;
    ax.y -= iResolution.y / iResolution.x/2.0;

    //Camera Speed.
    float time = sin ( ( iTime+10.0 ) /5.0 ) *3.0+5.0;

    //End Pos Camera
    ax *= 35.0/pow ( time,time );
    ax.x += 0.5;
    ax.y += iResolution.y/iResolution.x/2.0;
    ax.x += -0.101903;
    ax.y += 0.031983;

    vec2 m = ax;

    //Outline
    float ou = 0.2;
    float light = 1.0;

    //MandelBrot Funct.
    for ( int n = 0; n < 20000; n++ ) 

    	{  
    	ax = mat2 ( ax,-ax.y,ax.x ) * ax + m;
        if( dot ( ax,ax ) <2.0 ) {
    		ou += 0.1;

    	}

    }

    // Color

   	fragColor = vec4 ( cos ( ou+10.0 ) ,sin ( 0.0 ) ,floor ( 0.0) ,1.0 );

    float cm = aint* ( texture ( iChannel0, vec2 ( 0.02,0.26 ) ) .x-0.7 );
    float cx = aint* ( texture ( iChannel0, vec2 ( 0.2,0.4 ) ) .x-0.8 );

	float cs = ( 0.93+cm*cx ) * ( 0.35+abs ( sin ( iTime*colorspeed1 ) ) );
	float cc = ( 0.93+cm ) * ( 0.35+abs ( cos ( iTime*colorspeed2 ) ) );

	//fragColor = vec4 ( cs*cc*col, cs*col, cc*col, 1.0 );
	fragColor = fragColor.x*vec4 ( cs*cc*light, cs*light, cc*light, 1.0 );
}
