void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    float pi = 3.14159265359;
    float bpm = 1.0 / 60.0 * 97.0;
    float bt = t * bpm;

    float ts = 1.0 / 32.0;
    float t0 = texture(iChannel0, vec2(ts * 0.0, 0.25)).x;
    float t1 = texture(iChannel0, vec2(ts * 2.0, 0.25)).x;
    float t2 = texture(iChannel0, vec2(ts * 5.0, 0.25)).x;
    float t3 = texture(iChannel0, vec2(ts * 15.0, 0.25)).x;

    float t0p = pow(t0 - 0.4, 4.0) * 40.0;
    float t1p = pow(t1 - 0.2, 4.0) * 10.0;

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    //nuv.x *= sin((bt + 1.0 * (1.0 - abs(nuv.x))) * nuv.y * 0.1 + t0 * 0.2) * 0.0001;
    nuv.x *= abs(sin((1.0 - abs(nuv.y)) * (1.0 - abs(nuv.x)))) * 10.0;
    nuv.x *= sinh(nuv.y * 3.0 * abs(nuv.x) - sin(bt));

    vec3 col0 = vec3(0.84, 0.98, 0.57);
    vec3 col1 = vec3(0.96, 0.01, 0.32);
    vec3 col2 = vec3(0.04, 0.87, 0.85);

    vec3 c = vec3(0.0);

    c = mix(c, col0, 0.2 + t0p * 0.3);
    c = mix(c, col2, t1p * abs(nuv.x * 0.5) * 0.2);
    c = mix(c, col1, t3 - t2);

    c.xz += nuv.x * nuv.y * sin(bt);

    c.xyz += sin(bt * 2.0 + abs(nuv.x * 125.0)) * abs(nuv.x) * 0.1 - t2 * t0;

    c += col2 * (nuv.y * 0.2 + 0.2) * 1.0;

    fragColor.xyz = c;
}
