void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(0.1);
    vec3 col = vec3(0.4, 0.8, 1.0);
    vec3 col1 = vec3(0.7, 0.7, 0.2);

    float GlobalTime = mod(iTime, 9.0);

    vec2 r1 = r;

    float the = (3.0 - length(r) / 2.0) * 20.0;
    r.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    the = -iTime * 2.0;
    r1.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    if(r1.x >= -0.2 && r1.x <= 0.2 && r1.y <= 0.015 && r1.y >= -0.015){
    	ret = col1;
    }
    if(r1.x >= -0.015 && r1.x <= 0.015 && r1.y <= 0.2 && r1.y >= -0.2){
    	ret = col1;
    }

    the =  3.1415926535 / 4.0;
    r1.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    if(r1.x >= -0.2 && r1.x <= 0.2 && r1.y <= 0.015 && r1.y >= -0.015){
    	ret = col1;
    }
    if(r1.x >= -0.015 && r1.x <= 0.015 && r1.y <= 0.2 && r1.y >= -0.2){
    	ret = col1;
    }

    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }
    r.x *= -1.0;
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }
    r.y *= -1.0;
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }
    r.x *= -1.0;
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }

    GlobalTime = mod(iTime - 6.0, 9.0);
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }
    r.x *= -1.0;
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }
    r.y *= -1.0;
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }
    r.x *= -1.0;
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }

    GlobalTime = mod(iTime - 3.0, 9.0);
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }
    r.x *= -1.0;
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }
    r.y *= -1.0;
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }
    r.x *= -1.0;
    if(length(r - vec2(GlobalTime / 5.0)) <= GlobalTime / 30.0 ){
    	ret = col;
    }

    if(length(r) <=0.1){
    	ret = vec3(0.8, 0.2, 0.2);
    }

	fragColor = vec4(ret,1.0);
}
